yum install -y vim git make build-essential zlib-devel bzip2-devel openssl-devel ncurses-devel sqlite-devel readline-devel tk-devel gdbm-devel db4-devel libpcap-devel xz-devel && yum check-update && su - $1 -c "git clone https://github.com/pyenv/pyenv.git ~/.pyenv && echo 'export PYENV_ROOT="\$HOME/.pyenv"' >> ~/.bashrc && echo 'export PATH="\$PYENV_ROOT/bin:\$PATH"' >>~/.bashrc && echo 'eval \"\$(pyenv init -)\"' >> ~/.bashrc && source ~/.bashrc && ~/.pyenv/bin/pyenv install $2 && sleep 10 && ~/.pyenv/bin/pyenv global $2"
